package javaflowcontrol;

public class NetedForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0; i<10; i++)    //to handle rows
			{
			for(int j=0 ;j<=i ;j++)//to handle columns
		{
			System.out.print("*");
		}
	System.out.println();
		}
		}
		

}
